// Login.js

import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
const Login = () => {
  const history = useHistory();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // Lógica de inicio de sesión (aquí deberías verificar contra la API)
    // Por ahora, simplemente comprobamos si el nombre de usuario es "usuario" y la contraseña es "contrasena"
    if (username === 'usuario' && password === 'contrasena') {
      // Iniciar sesión exitosa, redirigir a la página del catálogo
      history.push('/catalogo');
    } else {
      // Mostrar mensaje de error o realizar otras acciones en caso de inicio de sesión fallido
      alert('Datos inválidos');
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <label>
        Usuario:
        <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
      </label>
      <br />
      <label>
        Contraseña:
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      </label>
      <br />
      <button onClick={handleLogin}>Iniciar Sesión</button>
    </div>
  );
};

export default Login;
