// Login.js
import React, { useState } from 'react';
import { useHistory } from 'react-router';


const Login = ({ setLoggedIn }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory();

  const handleLogin = () => {
    // Aquí deberías realizar la lógica de autenticación (por ejemplo, enviar una solicitud al backend)
    // En este ejemplo, simplemente comprobaremos si ambos campos no están vacíos
    if (username && password) {
      // Simulamos que el usuario ha iniciado sesión exitosamente
      setLoggedIn(true);
      // Redirigimos al usuario a la página principal
      history.push('/');
    } else {
      alert('Por favor, ingrese nombre de usuario y contraseña');
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form>
        <label>
          Usuario:
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
        </label>
        <br />
        <label>
          Contraseña:
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </label>
        <br />
        <button type="button" onClick={handleLogin}>
          Iniciar sesión
        </button>
      </form>
    </div>
  );
};

export default Login;
