import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DetalleProducto from './DetalleProducto';
import './ProductoCatalogo.css';
import FiltroCategoria from './FiltroCategoria';

const ProductoCatalogo = () => {
  const [productos, setProductos] = useState([]);
  const [originalProductos, setOriginalProductos] = useState([]);
  const [carretilla, setCarretilla] = useState([]); // Nuevo estado para la carretilla
  const [loading, setLoading] = useState(true);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [filterName, setFilterName] = useState('');
  const [filterMinPrice, setFilterMinPrice] = useState('');
  const [filterMaxPrice, setFilterMaxPrice] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [categorias, setCategorias] = useState([]);

  useEffect(() => {
    console.log("Iniciando la solicitud de productos y categorías...");
    axios.get('http://localhost:8080/allproductosdetallados')
      .then(response => {
        console.log("Productos obtenidos exitosamente:", response.data);
        setProductos(response.data);
        setOriginalProductos(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error("Error al obtener productos", error);
        setLoading(false);
      });

    axios.get('http://localhost:8080/allcategorias')
      .then(response => {
        console.log("Categorías obtenidas exitosamente:", response.data);
        setCategorias(response.data);
      })
      .catch(error => {
        console.error("Error al obtener categorías", error);
      });
  }, []);

  const handleFilter = () => {
    if (!filterName && !filterMinPrice && !filterMaxPrice && selectedCategory === null) {
      setProductos(originalProductos);
      return;
    }

    let filteredProducts = originalProductos.filter(producto => {
      const nameMatch = producto.prd_nombre.toLowerCase().includes(filterName.toLowerCase());
      const priceMatch = (
        (!filterMinPrice || producto.prd_precio >= parseFloat(filterMinPrice)) &&
        (!filterMaxPrice || producto.prd_precio <= parseFloat(filterMaxPrice))
      );
      const categoryMatch = selectedCategory === null || producto.cat_id === selectedCategory;
      return nameMatch && priceMatch && categoryMatch;
    });

    setProductos(filteredProducts);
  };

  const addToCarretilla = (producto) => {
    setCarretilla([...carretilla, producto]);
  };

  const removeFromCarretilla = (productoId) => {
    const updatedCarretilla = carretilla.filter(producto => producto.prd_id !== productoId);
    setCarretilla(updatedCarretilla);
  };

  return (
    <div className="producto-catalogo">
      <h1>Catálogo de productos</h1>

      <FiltroCategoria
        categorias={categorias}
        onCategoryChange={setSelectedCategory}
      />

      <div className="filtros">
        <input
          type="text"
          placeholder="Nombre del producto"
          value={filterName}
          onChange={e => setFilterName(e.target.value)}
        />
        <input
          type="number"
          placeholder="Precio mínimo"
          value={filterMinPrice}
          onChange={e => setFilterMinPrice(e.target.value)}
        />
        <input
          type="number"
          placeholder="Precio máximo"
          value={filterMaxPrice}
          onChange={e => setFilterMaxPrice(e.target.value)}
        />
        <button onClick={handleFilter}>Filtrar</button>
      </div>

      {loading ? (
        <p>Cargando productos...</p>
      ) : (
        <div className='grid-container'>
          {productos.map(producto => (
            <div key={producto.prd_id} className="producto-item">
              <img src={producto.prd_imagen} alt={""} />
              <h3>{producto.prd_nombre}</h3>
              <p>Precio: {producto.prd_precio}</p>
              <div className='button'>
                <button onClick={() => setSelectedProduct(producto)}>Ver detalles</button>
                <button onClick={() => addToCarretilla(producto)}>Agregar a la carretilla</button>
              </div>
            </div>
          ))}
        </div>
      )}

      {selectedProduct && (
        <DetalleProducto
          producto={selectedProduct}
          onClose={() => setSelectedProduct(null)}
          categoriaNombre={categorias.find(cat => cat.cat_id === selectedProduct.cat_id)?.cat_nombre}
        />
      )}

      <div className="carretilla">
        <h2>Carretilla</h2>
        <ul>
          {carretilla.map(producto => (
            <li key={producto.prd_id}>
              {producto.prd_nombre} - Precio: {producto.prd_precio}
              <button onClick={() => removeFromCarretilla(producto.prd_id)}>Eliminar</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ProductoCatalogo;
