import React from 'react';
import './Carrito.css';

const Carrito = ({ carrito, removeFromCart, decreaseQuantity, increaseQuantity }) => {
  return (
    <div className="carrito-sidebar">
      <h2>Carrito de compras</h2>
      <ul>
        {carrito.map(item => (
          <li key={item.prd_id}>
            {item.prd_nombre} - Precio: {item.prd_precio} - Cantidad: {item.cantidad}
            <button onClick={() => removeFromCart(item.prd_id)}>Eliminar</button>
            <button onClick={() => decreaseQuantity(item.prd_id)}>Reducir cantidad</button>
            <button onClick={() => increaseQuantity(item.prd_id)}>Aumentar cantidad</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Carrito;
