import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './DetalleProducto.css';

const DetalleProducto = ({ producto, onClose }) => {
    const [productos, setProductos] = useState([]);
  const [categorias, setCategorias] = useState([]);
  const [loading, setLoading] = useState(true);
  const [categoriaNombre, setCategoriaNombre] = useState('');

  useEffect(() => {
    console.log("Iniciando la solicitud de productos...");
    axios.get('http://localhost:8080/allproductosdetallados')
      .then(response => {
        console.log("Productos obtenidos exitosamente:", response.data);
        setProductos(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error("Error al obtener productos", error);
        setLoading(false);
      });

    axios.get('http://localhost:8080/categorias')
      .then(response => {
        setCategorias(response.data);
      })
      .catch(error => {
        console.error("Error al obtener categorías", error);
      });
  }, []);

  useEffect(() => {
    // Cuando las categorías y el producto están disponibles, actualizamos el nombre de la categoría
    if (producto.cat_id == categorias.cat_id) {
        categoriaNombre = categorias.cat_nombre
    }
  }, [categorias, producto.cat_id]);

  return (
    <div className="detalle-producto">
      <h2>{producto.prd_nombre}</h2>
      <p>Id del producto: {producto.prd_id}</p>
      <img src={producto.prd_imagen} alt="" />
      <p>{producto.prd_descripcion}</p>
      <p>Precio: {producto.prd_precio}</p>
      <p>Estado: {producto.prd_estado}</p>
      <p>Categoría: {categoriaNombre}</p>
      <button onClick={onClose}>Cerrar</button>
    </div>
  );
};

export default DetalleProducto;
