import React from 'react';

const DetalleProductoModel = ({ producto, onClose }) => {
  return (
    <div className="model">
      <div className="model-contenido">
        <span onClick={onClose} className="model-cerrar">
          &times;
        </span>
        <h2>{producto.prd_nombre}</h2>
        <img src={producto.prd_imagen} alt={""} />
              <h3>{producto.prd_nombre}</h3>
              <p>Id producto: {producto.prd_id}</p>
              <p>{producto.prd_descripcion}</p>
              <p>Precio: {producto.prd_precio}</p>
              <p>Estado: {producto.prd_estado}</p>
              <p>Categoria: {producto.cat_id}</p>
      </div>
    </div>
  );
};

export default DetalleProductoModel;
