import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './DetalleProducto.css';

const DetalleProducto = ({ producto, onClose }) => {
  const [categoria, setCategoria] = useState(null);

  useEffect(() => {

    const obtenerCategoria = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/categorias/${producto.cat_id}`);
        setCategoria(response.data);
      } catch (error) {
        console.error("Error al obtener detalles de la categoría", error);
      }
    };

    obtenerCategoria();
  }, [producto.cat_id]);

  return (
    <div className="detalle-producto">
      <h2>{producto.prd_nombre}</h2>
      <p>Id del producto: {producto.prd_id}</p>
      <img src={producto.prd_imagen} alt="" />
      <p>{producto.prd_descripcion}</p>
      <p>Precio: {producto.prd_precio}</p>
      <p>Estado: {producto.prd_estado}</p>
      <p>Categoría: {categoria ? categoria.nombre : 'Cargando...'}</p>
      <button onClick={onClose}>Cerrar</button>
    </div>
  );
};

export default DetalleProducto;
