import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ProductCatalog = () => {
  const [productos, setProductos] = useState([]);

  useEffect(() => {
    // Realiza una solicitud para obtener la lista de productos
    axios.get('http://localhost:8080/allproductos')
      .then(response => {
        setProductos(response.data);
      })
      .catch(error => {
        console.error('Error al obtener productos', error);
      });
  }, []);

  return (
    <div>
      <h1>Cat√°logo de productos</h1>
      <div>
        {productos.map(producto => (
          <div key={producto.prd_id}>
            <h3>{producto.prd_nombre}</h3>
            <p>{producto.prd_descripcion}</p>
            <p>Precio: ${producto.prd_precio}</p>
            <img src={producto.prd_imagen} alt={producto.prd_nombre} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductCatalog;
