import React from 'react';
import axios from 'axios';
import './DetalleProducto.css';

const DetalleProducto = ({ producto, categorias, onClose }) => {
  // Función para obtener el nombre de la categoría
  const obtenerNombreCategoria = () => {
    const categoria = categorias.find(cat => cat.cat_id === producto.cat_id);
    return categoria ? categoria.cat_nombre : 'Categoría Desconocida';
  };

  return (
    <div className="detalle-producto">
      <h2>{producto.prd_nombre}</h2>
      <p>Id del producto: {producto.prd_id}</p>
      <img src={producto.prd_imagen} alt="" />
      <p>{producto.prd_descripcion}</p>
      <p>Precio: {producto.prd_precio}</p>
      <p>Estado: {producto.prd_estado}</p>
      <p>Categoría: {obtenerNombreCategoria()}</p>
      <button onClick={onClose}>Cerrar</button>
    </div>
  );
};

export default DetalleProducto;
