// ProductoCatalogo.js
import React, { useState } from 'react';
import DetalleProductoModal from './DetalleProductoModel';
import './ProductoCatalogo.css';

const ProductoCatalogo = ({ productos }) => {
  const [modalVisible, setModalVisible] = useState(false);
  const [productoSeleccionado, setProductoSeleccionado] = useState(null);

  const mostrarDetalle = (producto) => {
    setProductoSeleccionado(producto);
    setModalVisible(true);
  };

  const cerrarModal = () => {
    setModalVisible(false);
    setProductoSeleccionado(null);
  };

  return (
    <div className="contenedor-catalogo">
      {productos.map((producto) => (
        <div key={producto.prd_id} className="producto-item">
          <img src={producto.prd_imagen} alt={""} />
          <h3>{producto.prd_nombre}</h3>
          <p>Precio: {producto.prd_precio}</p>
          <button onClick={() => mostrarDetalle(producto)}>Ver detalles</button>
        </div>
      ))}

      {modalVisible && (
        <DetalleProductoModal producto={productoSeleccionado} onClose={cerrarModal} />
      )}
    </div>
  );
};

export default ProductoCatalogo;
