// DetalleProducto.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './DetalleProducto.css';

const DetalleProducto = ({ producto, categorias, onClose }) => {
  const [categoria, setCategoria] = useState(null);

  useEffect(() => {
    const obtenerCategoria = async () => {
      try {
        // Intentar encontrar la categoría en el objeto categorias
        const categoriaEncontrada = categorias[producto.cat_id];

        if (categoriaEncontrada) {
          setCategoria(categoriaEncontrada);
        } else {
          // Si la categoría no está en el objeto, hacer una llamada a la API
          const response = await axios.get(`http://localhost:8080/categorias/${producto.cat_id}`);
          setCategoria(response.data.cat_nombre);
        }
      } catch (error) {
        console.error("Error al obtener detalles de la categoría", error);
      }
    };

    // Solo llamar a obtenerCategoria si no hay categoría o la categoría está cargando
    if (!categoria || categoria === 'Cargando...') {
      obtenerCategoria();
    }
  }, [producto.cat_id, categoria, categorias]);

  return (
    <div className="detalle-producto">
      <h2>{producto.prd_nombre}</h2>
      <p>Id del producto: {producto.prd_id}</p>
      <img src={producto.prd_imagen} alt="" />
      <p>{producto.prd_descripcion}</p>
      <p>Precio: {producto.prd_precio}</p>
      <p>Estado: {producto.prd_estado}</p>
      <p>Categoría: {categoria ? categoria : 'Cargando...'}</p>
      <button onClick={onClose}>Cerrar</button>
    </div>
  );
};

export default DetalleProducto;
